INSERT INTO `wp_options` VALUES("643", "newsletter", "a:20:{s:14:\"noconfirmation\";i:0;s:12:\"profile_text\";s:301:\"<p>Change your subscription preferences to get what you are most interested in.</p>\n    <p>If you change your email address, a confirmation email will be sent to activate it.</p>\n    </p>\n    {profile_form}\n    <p>To cancel your subscription, <a href=\'{unsubscription_confirm_url}\'>click here</a>.</p>\";s:21:\"profile_email_changed\";s:123:\"Your email has been changed, an activation email has been sent. Please follow the instructions to activate the new address.\";s:13:\"profile_error\";s:147:\"Your email is not valid or already in use by another subscriber or another generic error has been found. Check your data or contact the site owner.\";s:10:\"error_text\";s:173:\"<p>This subscription can\'t be completed, sorry. The email address is blocked or already subscribed. You should contact the owner to unlock that email address. Thank you.</p>\";s:18:\"subscribe_wp_users\";i:0;s:17:\"subscription_text\";s:19:\"{subscription_form}\";s:17:\"confirmation_text\";s:263:\"<p>You have successfully subscribed to the newsletter. You\'ll\nreceive a confirmation email in a few minutes. Please follow the\nlink to confirm your subscription. If the email takes\nmore than 15 minutes to appear in your mailbox, please check\nyour spam folder.</p>\";s:20:\"confirmation_subject\";s:53:\"Please confirm subscription - {blog_title} newsletter\";s:21:\"confirmation_tracking\";s:0:\"\";s:20:\"confirmation_message\";s:424:\"<p>Hi {name},</p>\n<p>A newsletter subscription request for this email address was\nreceived. Please confirm it by <a href=\"{subscription_confirm_url}\"><strong>clicking here</strong></a>. If you cannot\nclick the link, please use the following link:</p>\n\n<p>{subscription_confirm_url}</p>\n\n<p>If you did not make this subscription request, just ignore this\nmessage.</p>\n<p>Thank you!<br>\n<a href=\'{blog_url}\'>{blog_url}</a></p>\";s:14:\"confirmed_text\";s:62:\"<p>Your subscription has been confirmed! Thank you {name}!</p>\";s:17:\"confirmed_subject\";s:22:\"Welcome aboard, {name}\";s:17:\"confirmed_message\";s:277:\"<p>This message confirms your subscription to the {blog_title} newsletter.</p>\n<p>Thank you!<br>\n<a href=\'{blog_url}\'>{blog_url}</a></p>\n<p>To unsubscribe, <a href=\'{unsubscription_url}\'>click here</a>.  To change subscriber options,\n<a href=\'{profile_url}\'>click here</a>.</p>\";s:18:\"confirmed_tracking\";s:0:\"\";s:19:\"unsubscription_text\";s:111:\"<p>Please confirm that you want to unsubscribe by <a href=\'{unsubscription_confirm_url}\'>clicking here</a>.</p>\";s:25:\"unsubscription_error_text\";s:118:\"<p>The subscriber was not found, it probably has already been removed. No further actions are required. Thank you.</p>\";s:17:\"unsubscribed_text\";s:53:\"<p>Your subscription has been deleted. Thank you.</p>\";s:20:\"unsubscribed_subject\";s:8:\"Goodbye!\";s:20:\"unsubscribed_message\";s:195:\"<p>This message confirms that you have unsubscribed from the {blog_title} newsletter.</p>\n<p>You\'re welcome to sign up again anytime.</p>\n<p>Thank you!<br>\n<a href=\'{blog_url}\'>{blog_url}</a></p>\";}", "yes");
