INSERT INTO `wp_options` VALUES("905", "_transient_cff_p_tutorwizardsUStutorwizardsUS12meen", "{\"data\":[{\"id\":\"256573161203733_406225376238510\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"Even though school just started it is never too early to get ahead of the game! Get a Tutor Wizard today!\",\"type\":\"status\",\"status_type\":\"mobile_status_update\",\"created_time\":\"2015-09-02T17:20:59+0000\"},{\"id\":\"256573161203733_405960266265021\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"College Hack: When leaving for class give yourself an extra 10 minutes to get there. No need to add more stress to college by rushing to get there on time.\",\"type\":\"status\",\"status_type\":\"mobile_status_update\",\"created_time\":\"2015-09-01T17:36:09+0000\"},{\"id\":\"256573161203733_405251576335890\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"Getting a good nights sleep can do wonders! So shut down that computer, set down your cell phone, power off your TV, and catch some Z\'s! http:\\/\\/ow.ly\\/Rz5CB\",\"link\":\"http:\\/\\/ow.ly\\/Rz5CB\",\"name\":\"11 Surprising Health Benefits of Sleep\",\"caption\":\"health.com\",\"description\":\"How sleep benefits your heart, weight, mind, and more.\",\"type\":\"link\",\"status_type\":\"shared_story\",\"created_time\":\"2015-08-31T02:50:05+0000\"},{\"id\":\"256573161203733_404062683121446\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"We couldn\'t agree more! #NationalDogDay #TutorWizards http:\\/\\/ow.ly\\/RqwaG\",\"link\":\"http:\\/\\/ow.ly\\/RqwaG\",\"name\":\"32 Reasons Every Day Should Be National Dog Day\",\"caption\":\"buzzfeed.com\",\"description\":\"Aug. 26 is National Dog Day, but let\'s be real: EVERY day should be dog day.\",\"type\":\"link\",\"status_type\":\"shared_story\",\"created_time\":\"2015-08-27T00:30:12+0000\"},{\"id\":\"256573161203733_403999639794417\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"Have a great first day back to school! Make the most of it!\",\"link\":\"https:\\/\\/www.facebook.com\\/tutorwizardsUS\\/photos\\/a.266403913553991.1073741828.256573161203733\\/403999639794417\\/?type=3\",\"name\":\"Timeline Photos\",\"type\":\"photo\",\"status_type\":\"added_photos\",\"object_id\":\"403999639794417\",\"created_time\":\"2015-08-26T17:04:17+0000\"},{\"id\":\"256573161203733_403658339828547\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"College Hack: If you tend to be forgetful take advantage of your phone! You can set reminders and alarms so you won\'t forget important things like assignments, appointments, or even chores!\",\"type\":\"status\",\"status_type\":\"mobile_status_update\",\"created_time\":\"2015-08-25T17:25:39+0000\"},{\"id\":\"256573161203733_403134503214264\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"#MotivationalMonday #TutorWizards\",\"link\":\"https:\\/\\/www.facebook.com\\/tutorwizardsUS\\/photos\\/a.266403913553991.1073741828.256573161203733\\/403134503214264\\/?type=3\",\"name\":\"Timeline Photos\",\"type\":\"photo\",\"status_type\":\"added_photos\",\"object_id\":\"403134503214264\",\"created_time\":\"2015-08-24T17:11:21+0000\"},{\"id\":\"256573161203733_401738966687151\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"Tip Thursday: When registering for classes make sure you ask friends about different professors, their teaching styles, and how to be successful in the class. If this doesn\'t align with your needs, drop the class asap and find a new professor that better fits your needs!\",\"type\":\"status\",\"status_type\":\"mobile_status_update\",\"created_time\":\"2015-08-20T16:40:30+0000\"},{\"id\":\"256573161203733_401487816712266\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"\\\"The function of education is to teach one to think intensively and to think critically. Intelligence plus character- that is the goal of true education.\\\" -Martin Luther King Jr.\",\"type\":\"status\",\"status_type\":\"mobile_status_update\",\"created_time\":\"2015-08-19T17:31:16+0000\"},{\"id\":\"256573161203733_401264333401281\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"College Hack: Avoid working in your room. You don\'t want to mix where you work and where you relax, it tends to make it harder to focus. Instead try going to somewhere quiet like the library or a coffee shop!\",\"type\":\"status\",\"status_type\":\"mobile_status_update\",\"created_time\":\"2015-08-18T17:10:44+0000\"},{\"id\":\"256573161203733_400904116770636\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"#MotivationalMonday #TutorWizards\",\"link\":\"https:\\/\\/www.facebook.com\\/tutorwizardsUS\\/photos\\/a.266403913553991.1073741828.256573161203733\\/400904116770636\\/?type=3\",\"name\":\"Timeline Photos\",\"type\":\"photo\",\"status_type\":\"added_photos\",\"object_id\":\"400904116770636\",\"created_time\":\"2015-08-17T17:35:18+0000\"},{\"id\":\"256573161203733_400320603495654\",\"from\":{\"name\":\"Tutor Wizards\",\"id\":\"256573161203733\"},\"message\":\"Would you take your senior pictures at Taco Bell? Maybe McDonalds? Whataburger? \\nhttp:\\/\\/www.buzzfeed.com\\/davidmack\\/this-teen-girl-took-her-senior-pictures-inside-a-taco-bell#.ecyMdN1VN\",\"link\":\"http:\\/\\/www.buzzfeed.com\\/davidmack\\/this-teen-girl-took-her-senior-pictures-inside-a-taco-bell#.ecyMdN1VN\",\"name\":\"This Girl\'s Senior Photos At Taco Bell Are A Work Of Art\",\"caption\":\"www.buzzfeed.com\",\"description\":\"One high school relationship that is destined to last.\",\"type\":\"link\",\"status_type\":\"shared_story\",\"created_time\":\"2015-08-15T16:45:19+0000\"}],\"paging\":{\"previous\":\"https:\\/\\/graph.facebook.com\\/v2.6\\/256573161203733\\/posts?fields=id,from,message,message_tags,