INSERT INTO `wp_posts` VALUES("10", "1", "2017-07-25 06:47:14", "2017-07-25 06:47:14", "<div class=\"block_full block blockquote\">Tutor Wizard is an immersive virtual learning environment where students can access exciting educational content for <span style=\"color: #00aeed;\">Maths</span>, <span style=\"color: #00a482;\">Biology</span>, <span style=\"color: #f9bf51;\">Chemistry</span> &amp; <span style=\"color: #ed4b49;\">Physics</span> online anytime, anywhere.</div>\r\n<div class=\"row\">\r\n<div class=\"col-lg-6 col-md-6 block block_mini wow fadeInLeft\" data-wow-delay=\"1.5s\" data-wow-duration=\"1s\">\r\n<p>Students have a vast range of digital and interactive resources at their fingertips allowing them to become the leaders of their own learning as independent intellectuals striving for their own educational goals with Tutor Wizards dynamic system, suitable for any learning style and ability.</p>\r\n</div>\r\n<div class=\"col-lg-6 col-md-6 block block_mini wow fadeInRight\" data-wow-delay=\"1.5s\" data-wow-duration=\"1s\">\r\n<p>Tutor Wizard is an entirely student focused platform with minimal teaching input to help grow each individuals capabilities, improving the self-learning skills needed to succeed in higher education and the work place.</p>\r\n</div>\r\n</div>", "What is Tutor Wizard?", "", "publish", "closed", "closed", "", "home", "", "", "2017-07-26 05:26:47", "2017-07-26 05:26:47", "", "0", "http://localhost/tutorwizard/?page_id=10", "0", "page", "", "0");
